import React from 'react'
import "../Project/ProjectLogin.css"

function ProjectLogin() {

function change()
{
  const loginText = document.querySelector(".title-text .login");
const loginForm = document.querySelector("form.login");
const loginBtn = document.querySelector("label.login");
const signupBtn = document.querySelector("label.signup");
const signupLink = document.querySelector("form .signup-link a");
signupBtn.onclick = () => {
  loginForm.style.marginLeft = "-50%";
  loginText.style.marginLeft = "-50%";
};
loginBtn.onclick = () => {
  loginForm.style.marginLeft = "0%";
  loginText.style.marginLeft = "0%";
};
signupLink.onclick = () => {
  signupBtn.click();
  return false;
};
}

  return (
<div className='project' >
  <div className=" wrapper" style={{backgroundC}}>
    <div className="title-text">
      <div className="title login">Login Form</div>
      <div className="title signup">Signup Form</div>
    </div>
    <div className="form-container">
      <div className="slide-controls">
        <input type="radio" name="slide" id="login" defaultChecked />
        <input type="radio" name="slide" id="signup" />
        <label onClick={change} htmlFor="login" className="slide login">Login</label>
        <label onClick={change} htmlFor="signup" className="slide signup">Signup</label>
        <div className="slider-tab" />
      </div>
      <div className="form-inner">
        <form action="#" className="login">
          <div className="field">
            <input type="text" placeholder="Email Address" required />
          </div>
          <div className="field">
            <input type="password" placeholder="Password" required />
          </div>
          <div className="pass-link"><a href="#">Forgot password?</a></div>
          <div className="field btn">
            <div className="btn-layer" />
            <input type="submit" defaultValue="Login" />
          </div>
          <div className="signup-link">Not a member? <a href>Signup now</a></div>
        </form>
        <form action="#" className="signup">
          <div className="field">
            <input type="text" placeholder="Email Address" required />
          </div>
          <div className="field">
            <input type="password" placeholder="Password" required />
          </div>
          <div className="field">
            <input type="password" placeholder="Confirm password" required />
          </div>
          <div className="field btn">
            <div className="btn-layer" />
            <input type="submit" defaultValue="Signup" />
          </div>
        </form>
      </div>
    </div>
  </div>
</div>






  )
}

export default ProjectLogin
