import React from 'react'
import { useEffect } from 'react'

function Counter() {

    useEffect((PureCounter,Swiper)=>
    {
        new PureCounter();

        /**
         * Clients Slider
         */
        new Swiper('.clients-slider', {
          speed: 400,
          loop: true,
          autoplay: {
            delay: 5000,
            disableOnInteraction: false
          },
          slidesPerView: 'auto',
          pagination: {
            el: '.swiper-pagination',
            type: 'bullets',
            clickable: true
          },
          breakpoints: {
            320: {
              slidesPerView: 2,
              spaceBetween: 40
            },
            480: {
              slidesPerView: 3,
              spaceBetween: 60
            },
            640: {
              slidesPerView: 4,
              spaceBetween: 80
            },
            992: {
              slidesPerView: 6,
              spaceBetween: 120
            }
          }
        });
    })

  return (
    <div>

{/* ======= Stats Counter Section ======= */}
<section id="stats-counter" className="stats-counter">
  <div className="container" data-aos="fade-up">
    <div className="row gy-4 align-items-center">
      <div className="col-lg-6">
        <img src="assets/img/stats-img.svg" alt="" className="img-fluid" />
      </div>
      <div className="col-lg-6">
        <div className="stats-item d-flex align-items-center">
          <span data-purecounter-start={0} data-purecounter-end={232} data-purecounter-duration={1} className="purecounter" />
          <p><strong>Happy Clients</strong> consequuntur quae diredo para mesta</p>
        </div>{/* End Stats Item */}
        <div className="stats-item d-flex align-items-center">
          <span data-purecounter-start={0} data-purecounter-end={521} data-purecounter-duration={1} className="purecounter" />
          <p><strong>Projects</strong> adipisci atque cum quia aut</p>
        </div>{/* End Stats Item */}
        <div className="stats-item d-flex align-items-center">
          <span data-purecounter-start={0} data-purecounter-end={453} data-purecounter-duration={1} className="purecounter" />
          <p><strong>Hours Of Support</strong> aut commodi quaerat</p>
        </div>{/* End Stats Item */}
      </div>
    </div>
  </div>
</section>{/* End Stats Counter Section */}

      
    </div>
  )
}

export default Counter
